const TodoDetails = ({ todo }) => {
  return (
    <div className="todo-details">
      <h4>{todo.title}</h4>
      <p>
        <strong>{todo.author}</strong>
      </p>
      <p>{todo.content}</p>
    </div>
  );
};

export default TodoDetails;
